const THRESHOLDS = JSON.parse(env.get('THRESHOLDS'));
const telegramMessage = {
    payload:{
        chatId: env.get('CHAT_ID'),
        type:"message",
        content: ``
    }
}
const sensorAlerts = new Map()


msg.payload.forEach(data => {
    const value = data._value
    const topic = data.topic
    const sensorType = data.sensor_type
    const threshold_sensor = THRESHOLDS[sensorType];
    const splittedTopic = topic.split("/")
    // Initialize the count if it's not in the map
    if (!sensorAlerts.has(topic)) {
        sensorAlerts.set(topic, 0);
    }

    if (value > threshold_sensor) {
        sensorAlerts.set(topic, sensorAlerts.get(topic) + 1);
    }

    if (sensorAlerts.get(topic) >= 1) {
        telegramMessage.payload.content += `Alert! One or more ${sensorType} in ${splittedTopic[1]}, ${splittedTopic[2]} are exiding the threshold (${threshold_sensor})\n\n`;
    }
});

return telegramMessage;

if (msg.payload.length > 0) {
    let finaldMsg = "";

    const newMsg = { payload: {} }
    // Define thresholds for each _field
    const thresholds = {
        "air_quality": 350,
        "humidity": 42,
        "light_intensity": 700,
        "temperature": 28
    };

    // Create a map to store the count of exceeded thresholds for each topic
    const exceededThresholdsCount = new Map();

    // Function to extract the simplified topic
    function simplifyTopic(topic) {
        return topic.replace(/^.*?\/(forest_\d+\/area_\d+)$/, "$1");
    }

    // Check data against thresholds and generate alerts
    msg.payload.forEach(data => {
        const field = data["_field"];
        const value = data["_value"];
        const threshold_sensor = thresholds[field];
        const simplifiedTopic = simplifyTopic(data.topic);



        // Initialize the count if it's not in the map
        if (!exceededThresholdsCount.has(simplifiedTopic)) {
            exceededThresholdsCount.set(simplifiedTopic, 0);
        }

        switch (field) {
            case "air_quality":
                if (value > threshold_sensor) {
                    exceededThresholdsCount.set(simplifiedTopic, exceededThresholdsCount.get(simplifiedTopic) + 1);
                }
                break;
            case "humidity":
                if (value < threshold_sensor) {
                    exceededThresholdsCount.set(simplifiedTopic, exceededThresholdsCount.get(simplifiedTopic) + 1);
                }
                break;
            case "light_intensity":
                if (value > threshold_sensor) {
                    exceededThresholdsCount.set(simplifiedTopic, exceededThresholdsCount.get(simplifiedTopic) + 1);
                }
                break;
            case "temperature":
                if (value > threshold_sensor) {
                    exceededThresholdsCount.set(simplifiedTopic, exceededThresholdsCount.get(simplifiedTopic) + 1);
                }
            break;
            // Add additional cases for other fields if needed
        }


        // Increment the count for the current topic

        // Check if the count is higher than 2
        if (exceededThresholdsCount.get(simplifiedTopic) >= 2) {
            finaldMsg += `Alert in ${simplifiedTopic}. ${exceededThresholdsCount.get(simplifiedTopic)} exceeding thresholds \n`;
            // Additional logic for sending alerts can be added here
        }
    });
    newMsg.payload[`alert`] = finaldMsg;
    node.warn(finaldMsg);
    return newMsg
}
